import axios from 'axios';
import {
  Activity,
  CheckCircle2,
  ChevronRight,
  Dna,
  Pill,
  Search,
  ShieldAlert,
  ShieldCheck,
  Stethoscope,
  X
} from 'lucide-react';
import { useEffect, useState } from 'react';

const API_BASE = "http://127.0.0.1:8000";

export default function ModernPredictor() {
  const [symptoms, setSymptoms] = useState([]);
  const [selected, setSelected] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [results, setResults] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  // Fetch symptoms on mount
  useEffect(() => {
    axios.get(`${API_BASE}/symptoms`)
      .then(res => setSymptoms(res.data.symptoms))
      .catch(err => console.error("Backend unreachable. Ensure FastAPI is running on port 8000."));
  }, []);

  const toggleSymptom = (s) => {
    setSelected(prev => prev.includes(s) ? prev.filter(i => i !== s) : [...prev, s]);
  };

  const runAnalysis = async () => {
    if (selected.length === 0) return;
    setIsAnalyzing(true);
    setResults(null);

    try {
      const res = await axios.post(`${API_BASE}/predict`, { symptoms: selected });
      // res.data now contains { disease, description, precautions, medications }
      setResults(res.data);
    } catch (err) {
      console.error("Analysis Failed:", err);
    } finally {
      setIsAnalyzing(false);
    }
  };

  return (
    <div className="min-h-screen bg-[#FDFDFD] text-[#1E293B] font-sans selection:bg-indigo-100 antialiased">
      {/* Glassmorphism Navbar */}
      <nav className="sticky top-0 z-50 border-b border-slate-100 bg-white/70 backdrop-blur-xl">
        <div className="max-w-6xl mx-auto px-6 h-20 flex justify-between items-center">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-indigo-600 rounded-xl flex items-center justify-center shadow-lg shadow-indigo-200">
              <Dna size={22} className="text-white" />
            </div>
            <div>
              <h1 className="text-lg font-bold tracking-tight text-slate-900">NeuroDiagnostic <span className="text-indigo-600">AI</span></h1>
              <p className="text-[10px] uppercase tracking-widest text-slate-400 font-bold">Molecular Intelligence</p>
            </div>
          </div>
          <div className="flex items-center gap-2 px-4 py-2 bg-green-50 border border-green-100 rounded-full">
            <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse" />
            <span className="text-[11px] font-bold text-green-700 uppercase tracking-wider">System Online</span>
          </div>
        </div>
      </nav>

      <main className="max-w-6xl mx-auto px-6 py-12">
        <div className="grid lg:grid-cols-12 gap-12">

          {/* Left Column: Symptom Input */}
          <div className="lg:col-span-7 space-y-8">
            <section className="space-y-6">
              <div className="space-y-2">
                <h2 className="text-4xl font-black tracking-tight text-slate-900">Symptom Analysis</h2>
                <p className="text-slate-500 text-base">Select manifesting symptoms to trigger the random forest classifier.</p>
              </div>

              <div className="relative group">
                <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 group-focus-within:text-indigo-500 transition-colors" size={20} />
                <input 
                  type="text" 
                  placeholder="Search 130+ symptoms..." 
                  className="w-full pl-12 pr-6 py-5 bg-slate-50 border-2 border-transparent rounded-3xl focus:bg-white focus:border-indigo-100 focus:ring-4 focus:ring-indigo-50 transition-all outline-none text-slate-700 shadow-sm"
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>

              <div className="flex flex-wrap gap-2 overflow-y-auto max-h-[500px] p-2 custom-scrollbar">
                {symptoms.filter(s => s.toLowerCase().includes(searchTerm.toLowerCase())).map(s => {
                  const isActive = selected.includes(s);
                  return (
                    <button
                      key={s}
                      onClick={() => toggleSymptom(s)}
                      className={`group flex items-center gap-2 px-5 py-3 rounded-2xl text-xs font-bold transition-all border-2 ${
                        isActive 
                        ? 'bg-slate-900 border-slate-900 text-white shadow-xl shadow-slate-200 scale-[1.02]' 
                        : 'bg-white border-slate-100 text-slate-500 hover:border-indigo-200 hover:bg-indigo-50/30'
                      }`}
                    >
                      {isActive && <CheckCircle2 size={14} className="text-indigo-400" />}
                      {s.replace(/_/g, ' ')}
                    </button>
                  );
                })}
              </div>
            </section>
          </div>

          {/* Right Column: Results Dashboard */}
          <div className="lg:col-span-5">
            <div className="sticky top-32 space-y-6">
              <div className="bg-white rounded-[2.5rem] p-8 border border-slate-100 shadow-2xl shadow-slate-200/50">
                <div className="flex justify-between items-center mb-6">
                  <h3 className="text-[10px] font-black text-slate-400 uppercase tracking-[0.2em]">Live Profile</h3>
                  <span className="text-xs font-bold text-indigo-600 bg-indigo-50 px-3 py-1 rounded-full">{selected.length} Selected</span>
                </div>

                <div className="flex flex-wrap gap-2 mb-10 min-h-[60px]">
                  {selected.length === 0 ? (
                    <div className="w-full py-10 border-2 border-dashed border-slate-100 rounded-[2rem] flex flex-col items-center justify-center text-slate-300">
                      <Activity size={32} className="mb-3 opacity-20" />
                      <p className="text-xs font-bold uppercase tracking-widest">Input Data Required</p>
                    </div>
                  ) : (
                    selected.map(s => (
                      <div key={s} className="flex items-center gap-2 bg-slate-50 text-slate-700 px-4 py-2 rounded-xl text-[11px] font-bold border border-slate-200 animate-in fade-in zoom-in duration-300">
                        {s.replace(/_/g, ' ')}
                        <X size={14} className="text-slate-400 cursor-pointer hover:text-red-500 transition-colors" onClick={() => toggleSymptom(s)} />
                      </div>
                    ))
                  )}
                </div>

                <button 
                  onClick={runAnalysis}
                  disabled={selected.length === 0 || isAnalyzing}
                  className="w-full py-5 bg-indigo-600 text-white rounded-[1.5rem] font-bold flex items-center justify-center gap-3 hover:bg-indigo-700 active:scale-[0.98] transition-all disabled:opacity-20 shadow-xl shadow-indigo-200"
                >
                  {isAnalyzing ? (
                    <div className="flex items-center gap-3">
                      <div className="w-5 h-5 border-3 border-white/30 border-t-white rounded-full animate-spin" />
                      <span className="tracking-wide">Analyzing Neural Pathways...</span>
                    </div>
                  ) : (
                    <>
                      <span className="text-lg">Run Diagnostics</span>
                      <ChevronRight size={22} />
                    </>
                  )}
                </button>

                {/* Result Output */}
                {results && !isAnalyzing && (
                  <div className="mt-12 space-y-8 animate-in slide-in-from-bottom-10 duration-1000">
                    {/* Primary Diagnosis Card */}
                    <div className="relative overflow-hidden p-8 bg-slate-900 rounded-[2rem] text-white shadow-2xl">
                      <div className="relative z-10">
                        <div className="flex items-center gap-2 mb-4">
                            <Stethoscope size={16} className="text-indigo-400" />
                            <p className="text-indigo-400 text-[10px] font-black uppercase tracking-[0.3em]">Final Prediction</p>
                        </div>
                        <p className="text-4xl font-bold mb-4 tracking-tight leading-tight">{results.disease}</p>
                        <p className="text-slate-400 text-sm font-medium leading-relaxed border-t border-white/10 pt-6 italic">
                          "{results.description}"
                        </p>
                      </div>
                      <Activity className="absolute -right-6 -bottom-6 text-white/5 w-40 h-40" />
                    </div>

                    {/* Detailed Info Tabs */}
                    <div className="grid grid-cols-1 gap-4">
                      {/* Medications */}
                      <div className="p-6 bg-indigo-50/50 rounded-3xl border border-indigo-100">
                        <div className="flex items-center gap-2 mb-4">
                            <Pill size={18} className="text-indigo-600" />
                            <h4 className="text-xs font-black text-indigo-900 uppercase tracking-wider">Recommended Rx</h4>
                        </div>
                        <div className="grid grid-cols-1 gap-2">
                            {results.medications.map((m, i) => (
                                <div key={i} className="bg-white p-3 rounded-xl text-xs font-bold text-slate-700 shadow-sm border border-indigo-100/50">
                                    {m}
                                </div>
                            ))}
                        </div>
                      </div>

                      {/* Precautions */}
                      <div className="p-6 bg-white rounded-3xl border border-slate-100 shadow-sm">
                        <div className="flex items-center gap-2 mb-4">
                            <ShieldCheck size={18} className="text-emerald-600" />
                            <h4 className="text-xs font-black text-slate-400 uppercase tracking-wider">Precautionary Steps</h4>
                        </div>
                        <ul className="space-y-3">
                          {results.precautions.map((p, i) => (
                            <li key={i} className="text-xs text-slate-600 flex items-start gap-3 leading-snug">
                              <CheckCircle2 size={14} className="text-emerald-500 mt-0.5 shrink-0" />
                              <span className="font-medium">{p}</span>
                            </li>
                          ))}
                        </ul>
                      </div>
                    </div>

                    {/* Legal Disclaimer */}
                    <div className="p-5 bg-amber-50 rounded-2xl border border-amber-100/50 flex gap-4 items-center">
                      <ShieldAlert size={24} className="text-amber-600 shrink-0" />
                      <p className="text-[10px] text-amber-900/70 leading-relaxed font-bold uppercase tracking-tight">
                        Warning: This result is AI-generated and intended for informational purposes only. Consult a licensed medical professional.
                      </p>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}
