  ClipboardList,
  Droplets,
  HeartPulse,
  Pill, Search,
  ShieldAlert, ShieldCheck,
  Thermometer,
  User,
  Utensils,
  Wind, Zap
} from 'lucide-react';
import { useEffect, useMemo, useState } from 'react';

const API_BASE = "http://127.0.0.1:8000";

const CATEGORY_MAP = {
  "General": { icons: <Thermometer size={14}/>, symptoms: ["fatigue", "fever", "lethargy", "weight_loss", "high_fever", "chills", "malaise", "phlegm", "sweating", "mild_fever"] },
  "Skin": { icons: <Droplets size={14}/>, symptoms: ["itching", "skin_rash", "nodal_skin_eruptions", "pustule", "blackheads", "scurring", "yellowish_skin", "bruising", "peeling_skin"] },
  "Digestive": { icons: <Activity size={14}/>, symptoms: ["vomiting", "indigestion", "nausea", "abdominal_pain", "constipation", "diarrhoea", "stomach_pain", "acidity", "burning_micturition", "distention_of_abdomen"] },
  "Neurological": { icons: <BrainCircuit size={14}/>, symptoms: ["headache", "dizziness", "altered_sensorium", "unsteadiness", "lack_of_concentration", "visual_disturbances", "balance_errors"] },
  "Respiratory": { icons: <Wind size={14}/>, symptoms: ["continuous_sneezing", "cough", "breathlessness", "mucoid_sputum", "chest_pain"] },
  "Muscle/Joint": { icons: <Zap size={14}/>, symptoms: ["joint_pain", "muscle_wasting", "muscle_weakness", "back_pain", "neck_pain", "stiff_neck", "knee_pain", "swelling_joints"] }
};

export default function ModernPredictor() {
  const [backendSymptoms, setBackendSymptoms] = useState([]);
  const [selected, setSelected] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [activeCategory, setActiveCategory] = useState("All");
  const [results, setResults] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  useEffect(() => {
    axios.get(`${API_BASE}/symptoms`)
      .then(res => setBackendSymptoms(res.data.symptoms || []))
      .catch(err => console.error("Neural Link Offline"));
  }, []);

  const filteredSymptoms = useMemo(() => {
    return backendSymptoms.filter(s => {
      const matchesSearch = s.toLowerCase().includes(searchTerm.toLowerCase());
      if (activeCategory === "All") return matchesSearch;
      if (activeCategory === "Others") {
        const allKnown = Object.values(CATEGORY_MAP).flatMap(c => c.symptoms);
        return matchesSearch && !allKnown.includes(s);
      }
      return matchesSearch && CATEGORY_MAP[activeCategory]?.symptoms.includes(s);
    });
  }, [backendSymptoms, searchTerm, activeCategory]);

  const toggleSymptom = (s) => {
    setSelected(prev => prev.includes(s) ? prev.filter(i => i !== s) : [...prev, s]);
  };

  const runAnalysis = async () => {
    if (selected.length === 0) return;
    setIsAnalyzing(true);
    try {
      const res = await axios.post(`${API_BASE}/predict`, { symptoms: selected });
      setResults(res.data);
    } catch (err) {
      alert("Diagnostic Interrupt: Connectivity lost.");
    } finally {
      setTimeout(() => setIsAnalyzing(false), 800); // Small delay for UX feel
    }
  };

  return (
    <div className="min-h-screen bg-[#F1F5F9] text-[#1E293B] font-sans">
      {/* Header */}
      <header className="bg-white/70 backdrop-blur-md sticky top-0 z-50 border-b border-slate-200">
        <div className="max-w-7xl mx-auto px-6 h-20 flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className="p-2 bg-indigo-600 rounded-xl shadow-lg shadow-indigo-200">
              <HeartPulse className="text-white" size={24} />
            </div>
            <span className="font-black text-xl tracking-tighter uppercase italic">VITAL<span className="text-indigo-600">CORE</span></span>
          </div>
          <div className="flex items-center gap-4">
             <div className="h-8 w-[1px] bg-slate-200 mx-2" />
             <User size={20} className="text-slate-400" />
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-6 py-10 grid lg:grid-cols-12 gap-10">

        {/* SYMPTOM PANEL */}
        <section className="lg:col-span-5 space-y-6">
          <div className="bg-white rounded-[2.5rem] p-8 shadow-xl shadow-slate-200/50 border border-white">
            <h2 className="text-2xl font-black mb-6 flex items-center gap-3">
               <ClipboardList size={26} className="text-indigo-600"/> Symptom Log
            </h2>

            {/* Selected Pills Tray */}
            <div className="flex flex-wrap gap-2 mb-6 min-h-[40px]">
                {selected.length > 0 ? selected.map(s => (
                    <span key={s} className="px-3 py-1 bg-indigo-600 text-white text-[10px] font-bold rounded-full flex items-center gap-2 animate-in zoom-in-75">
                        {s.replace(/_/g, ' ')}
                        <X size={12} className="cursor-pointer" onClick={() => toggleSymptom(s)} />
                    </span>
                )) : <span className="text-xs text-slate-400 italic">No markers selected yet...</span>}
            </div>

            <div className="relative mb-6">
              <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
              <input 
                type="text" placeholder="Search markers..." 
                className="w-full pl-12 pr-4 py-4 bg-slate-50 rounded-2xl border-none focus:ring-2 focus:ring-indigo-500 outline-none transition-all font-medium text-sm"
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            {/* Category Navigation */}
            <div className="flex gap-2 overflow-x-auto pb-4 no-scrollbar">
              {["All", ...Object.keys(CATEGORY_MAP), "Others"].map(cat => (
                <button
                  key={cat} onClick={() => setActiveCategory(cat)}
                  className={`flex items-center gap-2 px-5 py-2.5 rounded-xl text-[10px] font-black transition-all whitespace-nowrap ${
                    activeCategory === cat ? 'bg-slate-900 text-white shadow-lg' : 'bg-slate-100 text-slate-500 hover:bg-slate-200'
                  }`}
                >
                  {CATEGORY_MAP[cat]?.icons} {cat.toUpperCase()}
                </button>
              ))}
            </div>

            <div className="h-[400px] overflow-y-auto pr-2 space-y-2">
              {filteredSymptoms.map(s => (
                <button
                  key={s} onClick={() => toggleSymptom(s)}
                  className={`w-full text-left p-4 rounded-2xl border transition-all flex justify-between items-center ${
                    selected.includes(s) ? 'bg-indigo-50 border-indigo-200 ring-1 ring-indigo-200' : 'bg-white border-slate-100 hover:border-slate-300 hover:translate-x-1'
                  }`}
                >
                  <span className="text-xs font-bold capitalize">{s.replace(/_/g, ' ')}</span>
                  {selected.includes(s) && <CheckCircle2 size={16} className="text-indigo-600" />}
                </button>
              ))}
            </div>

            <button 
              onClick={runAnalysis}
              disabled={selected.length === 0 || isAnalyzing}
              className="w-full mt-8 py-5 bg-indigo-600 text-white rounded-[1.5rem] font-black uppercase tracking-widest text-xs shadow-xl shadow-indigo-100 hover:bg-indigo-700 active:scale-95 transition-all flex items-center justify-center gap-3"
            >
              {isAnalyzing ? <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin" /> : <Zap size={18}/>}
              {isAnalyzing ? "Processing Data..." : "Run AI Inference"}
            </button>
          </div>
        </section>

        {/* RESULTS PANEL */}
        <section className="lg:col-span-7">
          {!results ? (
            <div className="h-full bg-white/50 border-2 border-dashed border-slate-300 rounded-[3rem] flex flex-col items-center justify-center p-20 text-center">
               <div className="w-20 h-20 bg-white rounded-3xl shadow-xl flex items-center justify-center mb-6">
                  <Activity size={40} className="text-slate-200" />
               </div>
               <h3 className="text-xl font-black text-slate-300 tracking-widest uppercase">Engine Idle</h3>
               <p className="text-slate-400 text-xs mt-4 max-w-xs font-medium">Input physiological symptoms to generate a diagnostic prediction based on the Random Forest model.</p>
            </div>
          ) : (
            <div className="space-y-6 animate-in fade-in slide-in-from-right-10 duration-700">
               {/* Hero Header */}
               <div className="bg-slate-900 rounded-[3rem] p-12 text-white shadow-2xl relative overflow-hidden">
                  <div className="absolute top-0 right-0 w-64 h-64 bg-indigo-500/20 rounded-full blur-[100px] -mr-20 -mt-20" />
                  <div className="relative z-10">
                    <span className="text-indigo-400 font-black text-[10px] uppercase tracking-[0.4em]">Inference Result</span>
                    <h1 className="text-6xl font-black mt-2 mb-8 tracking-tighter">{results.disease}</h1>
                    <div className="flex gap-4 p-6 bg-white/5 border border-white/10 rounded-3xl backdrop-blur-md">
                        <Info size={24} className="text-indigo-400 shrink-0" />
                        <p className="text-xs text-slate-300 leading-relaxed italic">{results.description}</p>
                    </div>
                  </div>
               </div>

               {/* Action Grid */}
               <div className="grid md:grid-cols-2 gap-6">
                 <ResultTile title="Prescription" icon={<Pill/>} color="rose" list={results.medications} />
                 <ResultTile title="Care Steps" icon={<ShieldCheck/>} color="emerald" list={results.precautions} />
                 <ResultTile title="Nutrients" icon={<Utensils/>} color="amber" list={results.diets} />
                 <ResultTile title="Recovery" icon={<Activity/>} color="blue" content={results.workout} />
               </div>

               <div className="p-6 bg-amber-50 rounded-3xl border border-amber-100 flex gap-4 items-start">
                  <ShieldAlert className="text-amber-600 shrink-0" size={24}/>
                  <div>
                    <h4 className="text-[10px] font-black text-amber-800 uppercase tracking-widest mb-1">Clinical Disclaimer</h4>
                    <p className="text-[10px] text-amber-700 leading-relaxed font-bold">This is an AI-generated screening result. It is not a definitive medical diagnosis. Please consult a healthcare professional for clinical validation.</p>
                  </div>
               </div>
            </div>
          )}
        </section>
      </main>
    </div>
  );
}

// Reusable Tile Component for Cleanliness
function ResultTile({ title, icon, color, list, content }) {
    const themes = {
        rose: "text-rose-600 bg-rose-50 border-rose-100",
        emerald: "text-emerald-600 bg-emerald-50 border-emerald-100",
        amber: "text-amber-600 bg-amber-50 border-amber-100",
        blue: "text-blue-600 bg-blue-50 border-blue-100"
    };

    return (
        <div className="bg-white p-8 rounded-[2.5rem] border border-slate-200 shadow-sm transition-all hover:shadow-md">
            <div className={`flex items-center gap-3 mb-6 font-black text-[10px] uppercase tracking-[0.2em] ${themes[color].split(' ')[0]}`}>
                <div className={`p-2 rounded-xl ${themes[color]}`}>{icon}</div>
                {title}
            </div>
            {content ? (
                <p className="text-xs font-bold text-slate-600 leading-relaxed">{content}</p>
            ) : (
                <div className="space-y-2">
                    {list?.map((item, i) => (
                        <div key={i} className={`px-4 py-2.5 rounded-xl text-[10px] font-black border capitalize ${themes[color]}`}>
                            {item}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}
