import axios from 'axios';
import {
  Activity,
  BrainCircuit,
  CheckCircle2,
  ClipboardList, LayoutGrid,
  Pill, Search,
  ShieldCheck, Stethoscope, Utensils
} from 'lucide-react';
import { useEffect, useMemo, useState } from 'react';

const API_BASE = "http://127.0.0.1:8000";

// This mapping acts as a "Filter" for the backend data
const CATEGORY_MAP = {
  "General": ["fatigue", "fever", "lethargy", "weight_loss", "high_fever", "chills", "malaise", "phlegm", "sweating", "mild_fever"],
  "Skin": ["itching", "skin_rash", "nodal_skin_eruptions", "pustule", "blackheads", "scurring", "yellowish_skin", "bruising", "peeling_skin"],
  "Digestive": ["vomiting", "indigestion", "nausea", "abdominal_pain", "constipation", "diarrhoea", "stomach_pain", "acidity", "burning_micturition", "distention_of_abdomen"],
  "Neurological": ["headache", "dizziness", "altered_sensorium", "unsteadiness", "lack_of_concentration", "visual_disturbances", "balance_errors"],
  "Respiratory": ["continuous_sneezing", "cough", "breathlessness", "mucoid_sputum", "chest_pain"],
  "Muscle/Joint": ["joint_pain", "muscle_wasting", "muscle_weakness", "back_pain", "neck_pain", "stiff_neck", "knee_pain", "swelling_joints"]
};

export default function ModernPredictor() {
  const [backendSymptoms, setBackendSymptoms] = useState([]); // Raw data from Python
  const [selected, setSelected] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [activeCategory, setActiveCategory] = useState("All");
  const [results, setResults] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  // 1. FETCH FROM BACKEND
  useEffect(() => {
    axios.get(`${API_BASE}/symptoms`)
      .then(res => {
        // res.data.symptoms comes directly from your Training.csv columns
        setBackendSymptoms(res.data.symptoms || []);
      })
      .catch(err => console.error("Backend unreachable. Check FastAPI server."));
  }, []);

  // 2. DYNAMIC FILTERING LOGIC
  const filteredSymptoms = useMemo(() => {
    return backendSymptoms.filter(s => {
      const matchesSearch = s.toLowerCase().includes(searchTerm.toLowerCase());

      if (activeCategory === "All") return matchesSearch;

      if (activeCategory === "Others") {
        // Find symptoms that aren't in any defined category
        const allKnownSymptoms = Object.values(CATEGORY_MAP).flat();
        return matchesSearch && !allKnownSymptoms.includes(s);
      }

      // Match against the CATEGORY_MAP
      return matchesSearch && CATEGORY_MAP[activeCategory]?.includes(s);
    });
  }, [backendSymptoms, searchTerm, activeCategory]);

  const toggleSymptom = (s) => {
    setSelected(prev => prev.includes(s) ? prev.filter(i => i !== s) : [...prev, s]);
  };

  const runAnalysis = async () => {
    if (selected.length === 0) return;
    setIsAnalyzing(true);
    try {
      const res = await axios.post(`${API_BASE}/predict`, { symptoms: selected });
      setResults(res.data);
    } catch (err) {
      alert("Analysis Failed. Ensure Backend is running.");
    } finally {
      setIsAnalyzing(false);
    }
  };

  return (
    <div className="min-h-screen bg-[#F8FAFC] text-slate-900 font-sans antialiased">
      {/* Navbar */}
      <nav className="sticky top-0 z-50 bg-white/80 backdrop-blur-xl border-b border-slate-200">
        <div className="max-w-[1400px] mx-auto px-6 h-16 flex justify-between items-center">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-indigo-600 rounded-xl flex items-center justify-center shadow-lg shadow-indigo-200">
              <BrainCircuit size={22} className="text-white" />
            </div>
            <h1 className="text-lg font-black tracking-tighter uppercase">Neuro<span className="text-indigo-600">AI</span></h1>
          </div>
          <div className="hidden sm:flex items-center gap-2 px-3 py-1 bg-emerald-50 rounded-full border border-emerald-100">
            <div className="w-2 h-2 bg-emerald-500 rounded-full animate-pulse" />
            <span className="text-[10px] font-black text-emerald-700 uppercase tracking-widest">Neural Link Active</span>
          </div>
        </div>
      </nav>

      <main className="max-w-[1400px] mx-auto px-6 py-8">
        <div className="grid lg:grid-cols-12 gap-8">

          {/* LEFT COLUMN: SYMPTOM SELECTION */}
          <div className="lg:col-span-5 space-y-6">
            <div className="bg-white rounded-[2rem] p-8 shadow-sm border border-slate-200">
              <div className="flex items-center justify-between mb-6">
                <h2 className="text-xl font-bold tracking-tight flex items-center gap-2">
                  <ClipboardList className="text-indigo-600" size={20} />
                  Symptom Analysis
                </h2>
                <button 
                   onClick={() => setSelected([])}
                   className="text-[10px] font-bold text-rose-500 hover:underline uppercase"
                >
                  Clear All
                </button>
              </div>

              {/* Search */}
              <div className="relative mb-6">
                <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={16} />
                <input 
                  type="text" 
                  placeholder="Search symptoms..." 
                  className="w-full pl-11 pr-4 py-3 bg-slate-50 border border-slate-200 rounded-xl focus:border-indigo-500 outline-none transition-all text-sm"
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>

              {/* Category Toggles */}
              <div className="flex overflow-x-auto gap-2 mb-6 pb-2 scrollbar-hide">
                {["All", ...Object.keys(CATEGORY_MAP), "Others"].map(cat => (
                  <button
                    key={cat}
                    onClick={() => setActiveCategory(cat)}
                    className={`px-4 py-2 rounded-full text-[10px] font-extrabold whitespace-nowrap transition-all ${
                      activeCategory === cat 
                      ? 'bg-indigo-600 text-white shadow-md shadow-indigo-100' 
                      : 'bg-slate-100 text-slate-500 hover:bg-slate-200'
                    }`}
                  >
                    {cat.toUpperCase()}
                  </button>
                ))}
              </div>

              {/* Results List */}
              <div className="h-[380px] overflow-y-auto pr-2 grid grid-cols-1 gap-2">
                {filteredSymptoms.map(s => {
                  const isActive = selected.includes(s);
                  return (
                    <button
                      key={s}
                      onClick={() => toggleSymptom(s)}
                      className={`text-left px-4 py-3 rounded-xl text-xs font-bold transition-all border ${
                        isActive 
                        ? 'bg-indigo-50 border-indigo-200 text-indigo-700' 
                        : 'bg-white border-slate-100 text-slate-600 hover:border-slate-300'
                      }`}
                    >
                      <div className="flex items-center justify-between">
                         <span className="capitalize">{s.replace(/_/g, ' ')}</span>
                         {isActive && <CheckCircle2 size={14} />}
                      </div>
                    </button>
                  );
                })}
              </div>

              <button 
                onClick={runAnalysis}
                disabled={selected.length === 0 || isAnalyzing}
                className="w-full mt-6 py-4 bg-slate-900 text-white rounded-xl font-black text-xs uppercase tracking-[0.2em] shadow-xl hover:bg-black disabled:opacity-20 transition-all flex items-center justify-center gap-2"
              >
                {isAnalyzing ? "Analyzing Neural Patterns..." : "Run Diagnostic"}
              </button>
            </div>
          </div>

          {/* RIGHT COLUMN: RESULTS */}
          <div className="lg:col-span-7">
            {!results && !isAnalyzing ? (
              <div className="h-full min-h-[500px] bg-white border border-slate-200 rounded-[2rem] flex flex-col items-center justify-center p-12 text-center">
                <div className="w-20 h-20 bg-slate-50 rounded-full flex items-center justify-center mb-6">
                    <LayoutGrid className="text-slate-300" size={32} />
                </div>
                <h3 className="text-sm font-black text-slate-400 uppercase tracking-widest">System Standby</h3>
                <p className="text-slate-400 text-[11px] max-w-[200px] mt-2 font-bold uppercase leading-relaxed">Select symptoms to initiate the inference engine.</p>
              </div>
            ) : isAnalyzing ? (
                <div className="space-y-4">
                    <div className="h-64 bg-white animate-pulse rounded-[2rem] border border-slate-200" />
                    <div className="grid grid-cols-2 gap-4">
                        <div className="h-40 bg-white animate-pulse rounded-[2rem] border border-slate-200" />
                        <div className="h-40 bg-white animate-pulse rounded-[2rem] border border-slate-200" />
                    </div>
                </div>
            ) : (
              <div className="space-y-6 animate-in fade-in slide-in-from-bottom-4 duration-500">
                {/* Primary Diagnosis */}
                <div className="bg-white border border-slate-200 rounded-[2.5rem] p-10 shadow-sm overflow-hidden relative">
                  <div className="absolute top-0 right-0 p-8 opacity-5">
                      <Stethoscope size={120} />
                  </div>
                  <span className="text-indigo-600 font-black text-[10px] uppercase tracking-[0.3em]">Neural Prediction</span>
                  <h1 className="text-5xl font-black text-slate-900 mt-2 mb-6 tracking-tighter">{results.disease}</h1>
                  <div className="p-5 bg-slate-50 rounded-2xl border-l-4 border-indigo-500">
                    <p className="text-xs text-slate-600 font-bold leading-relaxed italic">"{results.description}"</p>
                  </div>
                </div>

                {/* Info Grid */}
                <div className="grid md:grid-cols-2 gap-4">
                  <ResultCard title="Medical Protocol" icon={<Pill size={18}/>} color="rose" data={results.medications} />
                  <ResultCard title="Safety Steps" icon={<ShieldCheck size={18}/>} color="emerald" data={results.precautions} />
                  <ResultCard title="Dietary Plan" icon={<Utensils size={18}/>} color="amber" data={results.diets} />
                  <ResultCard title="Recovery" icon={<Activity size={18}/>} color="blue" isText data={results.workout} />
                </div>
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

// Sub-component for Cleaner Result Cards
function ResultCard({ title, icon, color, data, isText = false }) {
    const colorClasses = {
        rose: "text-rose-600 bg-rose-50 border-rose-100",
        emerald: "text-emerald-600 bg-emerald-50 border-emerald-100",
        amber: "text-amber-600 bg-amber-50 border-amber-100",
        blue: "text-blue-600 bg-blue-50 border-blue-100"
    };

    return (
        <div className="bg-white p-6 rounded-[2rem] border border-slate-200 shadow-sm">
            <div className={`flex items-center gap-2 mb-4 font-black text-[10px] uppercase tracking-wider ${colorClasses[color].split(' ')[0]}`}>
                {icon} {title}
            </div>
            {isText ? (
                <p className="text-[11px] font-bold text-slate-600 leading-relaxed">{data}</p>
            ) : (
                <div className="flex flex-wrap gap-2">
                    {Array.isArray(data) && data.map((item, i) => (
                        <span key={i} className={`px-3 py-1.5 rounded-lg text-[10px] font-black border ${colorClasses[color]}`}>
                            {item}
                        </span>
                    ))}
                </div>
            )}
        </div>
    );
}
