import axios from 'axios';
import { ChevronRight, FlaskConical, Search, ShieldAlert, Thermometer } from 'lucide-react';
import { useEffect, useState } from 'react';

const API_BASE = "http://127.0.0.1:8000";

export default function ModernPredictor() {
  const [symptoms, setSymptoms] = useState([]);
  const [selected, setSelected] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [results, setResults] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  useEffect(() => {
    axios.get(`${API_BASE}/symptoms`).then(res => setSymptoms(res.data.symptoms));
  }, []);

  const toggleSymptom = (s) => {
    setSelected(prev => prev.includes(s) ? prev.filter(i => i !== s) : [...prev, s]);
  };

 const runAnalysis = async () => {
  if (selected.length === 0) return;
  setIsAnalyzing(true);
  setResults(null); // Clear old results before fetching new ones

  try {
    // 1. Send the POST request to FastAPI
    const res = await axios.post(`${API_BASE}/predict`, { 
      symptoms: selected  // Ensure this matches the Pydantic model in Python
    });

    // 2. Log the response to your browser console to verify it arrived
    console.log("Prediction Received:", res.data);

    // 3. Update the state
    // Root Fix: Make sure the keys here match your FastAPI return dict
    setResults(res.data); 

  } catch (err) {
    console.error("API Error:", err.response ? err.response.data : err.message);
    alert("Connection to Backend failed. Check if FastAPI is running.");
  } finally {
    setIsAnalyzing(false);
  }
};

  return (
    <div className="min-h-screen bg-[#F8FAFC] text-slate-900 font-sans antialiased">
      {/* Navigation */}
      <nav className="border-b bg-white/80 backdrop-blur-md sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
          <div className="flex items-center gap-2 font-bold text-xl text-indigo-600">
            <div className="p-1.5 bg-indigo-600 rounded-lg text-white">
              <FlaskConical size={20} />
            </div>
            <span>NeuroDiagnostic <span className="text-slate-400 font-normal">AI</span></span>
          </div>
          <div className="text-xs font-medium px-3 py-1 bg-green-100 text-green-700 rounded-full">System Online</div>
        </div>
      </nav>

      <main className="max-w-7xl mx-auto px-6 py-10 grid lg:grid-cols-12 gap-10">

        {/* Left Column: Symptom Selection */}
        <div className="lg:col-span-7 space-y-6">
          <div className="bg-white rounded-3xl p-8 shadow-sm border border-slate-100">
            <h2 className="text-2xl font-bold mb-6 flex items-center gap-3">
              <Thermometer className="text-indigo-500" />
              Identify Symptoms
            </h2>

            <div className="relative mb-8">
              <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400" size={18} />
              <input 
                type="text" 
                placeholder="Search symptoms (e.g., headache, muscle_pain)..."
                className="w-full pl-12 pr-6 py-4 bg-slate-50 border-none rounded-2xl focus:ring-2 focus:ring-indigo-500 transition-all outline-none text-slate-700"
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>

            <div className="flex flex-wrap gap-2 overflow-y-auto max-h-[400px] pr-2 custom-scrollbar">
              {symptoms.filter(s => s.includes(searchTerm)).map(s => (
                <button
                  key={s}
                  onClick={() => toggleSymptom(s)}
                  className={`px-4 py-2 rounded-xl text-sm font-medium transition-all duration-200 border ${
                    selected.includes(s)
                    ? 'bg-indigo-600 border-indigo-600 text-white shadow-lg shadow-indigo-200 scale-95'
                    : 'bg-white border-slate-200 text-slate-600 hover:border-indigo-300 hover:bg-indigo-50'
                  }`}
                >
                  {s.replace(/_/g, ' ')}
                </button>
              ))}
            </div>
          </div>
        </div>

        {/* Right Column: Dashboard */}
        <div className="lg:col-span-5 space-y-6">
          <div className="bg-white rounded-3xl p-8 shadow-sm border border-slate-100 sticky top-28">
            <h3 className="text-lg font-bold mb-4 text-slate-500 uppercase tracking-wider text-xs">Selected Sequence</h3>
            <div className="flex flex-wrap gap-2 mb-8">
              {selected.length === 0 && <p className="text-slate-400 italic text-sm">Waiting for input data...</p>}
              {selected.map(s => (
                <div key={s} className="bg-indigo-50 text-indigo-700 px-3 py-1 rounded-lg text-xs font-semibold animate-in zoom-in duration-300">
                  {s.replace(/_/g, ' ')}
                </div>
              ))}
            </div>

            <button 
              onClick={runAnalysis}
              disabled={selected.length === 0 || isAnalyzing}
              className="w-full py-4 bg-slate-900 text-white rounded-2xl font-bold flex items-center justify-center gap-2 hover:bg-indigo-700 transition-all disabled:opacity-20 disabled:grayscale group"
            >
              {isAnalyzing ? "Processing..." : "Generate Analysis"}
              <ChevronRight size={18} className="group-hover:translate-x-1 transition-transform" />
            </button>

            {results && !isAnalyzing && (
              <div className="mt-10 space-y-6 animate-in slide-in-from-bottom-4 duration-500">
                <div className="p-6 bg-indigo-600 rounded-2xl text-white shadow-xl shadow-indigo-200">
                  <p className="text-indigo-200 text-xs font-bold uppercase mb-1">Primary Consensus</p>
                  <p className="text-2xl font-bold leading-tight">{results.random_forest}</p>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div className="p-4 bg-slate-50 rounded-2xl border border-slate-100">
                    <p className="text-[10px] text-slate-400 font-bold uppercase mb-1 text-center">Neural Net Path</p>
                    <p className="text-sm font-bold text-slate-700 text-center">{results.decision_tree}</p>
                  </div>
                  <div className="p-4 bg-slate-50 rounded-2xl border border-slate-100">
                    <p className="text-[10px] text-slate-400 font-bold uppercase mb-1 text-center">Probabilistic</p>
                    <p className="text-sm font-bold text-slate-700 text-center">{results.naive_bayes}</p>
                  </div>
                </div>

                <div className="flex items-start gap-3 p-4 bg-amber-50 rounded-2xl border border-amber-100 text-amber-800 text-xs leading-relaxed">
                  <ShieldAlert size={24} className="shrink-0" />
                  <span>The AI identification is based on pattern recognition from historical datasets. It should not be treated as a clinical diagnosis.</span>
                </div>
              </div>
            )}
          </div>
        </div>
      </main>
    </div>
  );
}
